import React, { useState } from 'react';
import axios from 'axios';
import WeatherDisplay from './weatherDisplay';
import SearchBar from './searchBar';

function App() {
    const [weatherData, setWeatherData] = useState(null);

    const handleSearch = (city) => {
        const API_ENDPOINT = 'http://api.weatherapi.com/v1/current.json'; // Replace with your API endpoint
        const API_KEY = '890217156a7e13ed492ba979545975cb'; // Replace with your API key

        axios.get(`${API_ENDPOINT}?key=${API_KEY}&q=${city}`)
            .then(response => {
                setWeatherData(response.data);
            })
            .catch(error => {
                console.error("Error fetching data: ", error);
                setWeatherData(null); // Reset state in case of an error
            });
    };

    return (
        <div className="App">
            <SearchBar onSearch={handleSearch} />
            {weatherData && <WeatherDisplay data={weatherData} />}
        </div>
    );
}

export default App;
